sp_widget.client_script:
function($scope,$rootScope) {
		/* widget controller */
		var c = this;

		$rootScope.$on('selectNote', function(event,data) {
			c.server.get({
				action: 'getNote',
				noteID: data
			}).then(function(r) {
				c.data.title = r.data.note.title;
				c.data.note = r.data.note.note;
				c.data.noteID = r.data.note.number;
			});
		});
	c.updateNote = function() {
		console.log("client script update note function call");
		c.server.get({
			action: 'updateNote',
			noteID: c.data.noteID,
			noteBody: c.data.note,
			noteTitle: c.data.title
		}).then(function(r) {
		});
	}
	}

sp_widget.script:
(function() {
	/* populate the 'data' object */

	if (input && input.noteID) {

		var note = new GlideRecord('x_58872_needit_note');
		if (note.get("number",input.noteID)) {
			if (input.action == 'getNote') {
				console.log("action"+input.action);
				data.note = {};
				$sp.getRecordValues(data.note, note, "title,note, number");

			}else if (input.action == 'updateNote') { 
				console.log("action"+input.action);
				note.title = input.noteTitle; 
				note.note = input.noteBody; 
				note.update(); 
			}
		}
	}
})();
